sys_variable_value.value:
(function execute(inputs, outputs) {

  var ignore = false;
  var filePath = inputs.file_path;
  var fileName = inputs.file_path.substring(inputs.file_path.lastIndexOf('/') + 1, inputs.file_path.length);

  if (!ignore && filePath.indexOf('sketch') !== -1) {
    ignore = true;
  }

  if (!ignore && filePath.indexOf('.DS_Store') !== -1) {
    ignore = true;
  }

  if (!ignore && filePath.startsWith('__MACOSX')) {
    ignore = true;
  }

  if (!ignore && fileName.startsWith('._')) {
    ignore = true;
  }

  if (!ignore) {
    outputs.type = filePath.indexOf('Shadow') !== -1 ? 'device_with_shadow' : 'device';
  } else {
    outputs.type = 'ignore';
  }

  outputs.file_name = fileName;

})(inputs, outputs);

