sys_variable_value.value:
(function execute(inputs, outputs) {

  if (inputs.attachment_sys_id !== '') {
    inputs.device.setValue('file', inputs.attachment_sys_id);
    inputs.device.update();

    var helper = new global.MMRGHelper();
    helper.hideAttachmentInField(inputs.attachment_sys_id);
  }

})(inputs, outputs);

